meta.script:
OU_BRAND = 'OU Campus';
// Ensure that sensitive pages load securely.
// Make sure the set of affected routes is the same as that in the router!
(function() {
	var hash = window.location.hash;
	var needsToBeSecure = function () {
		var params = hash.replace("#","").split("/");
		var secureScreenRegex = /^(newuser|newsite|addons)$/;
		var secureWithIdRegex = /^(users|sites|clonesite)$/;
		if (params[3] == 'assets' && params[5] == "submission")
			return true;
		if (params[3] != "setup")
			return false;
		if (params[4] == undefined)
			return true;
		if (secureScreenRegex.test(params[4]))
			return true;
		if (secureWithIdRegex.test(params[4]) && params[5])
			return true;
		return false;
	};
	if (needsToBeSecure()) {
		window.location.href = "https://" + window.location.host + "/10/" + hash;
	} else {
		window.location.href = "http://" + window.location.host + "/10/" + hash;
	}
})();

